import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import Navbar from './Navbar';

const Dashboard = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();
  const email = localStorage.getItem("userEmail");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && isValidFileType(selectedFile)) {
      setFile(selectedFile);
      setErrorMessage('');
    } else {
      setFile(null);
      setErrorMessage('Please select a valid file type (.txt, .doc, .docx, or .pdf)');
    }
  };

  const isValidFileType = (file) => {
    const validTypes = ['.txt', '.doc', '.docx', '.pdf'];
    return validTypes.some(type => file.name.toLowerCase().endsWith(type));
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setErrorMessage("Please select a file first");
      return;
    }

    setLoading(true);
    setErrorMessage('');
    setUploadProgress(0);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("email", email);    try {
      const user = JSON.parse(localStorage.getItem('user'));
      if (!user || !user.id || !user.token) {
        throw new Error('User not authenticated');
      }

      const res = await api.post(`/api/files/upload/user?userId=${user.id}`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          "Accept": "*/*",
          "Authorization": `Bearer ${user.token}`
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
        }
      });
      
      if (res.data && res.data.success) {
        navigate("/result", { state: res.data });
      } else {
        throw new Error(res.data?.message || "Upload failed");
      }    } catch (error) {
      console.error("Upload failed:", error);
      console.error("Response data:", error.response?.data);
      console.error("Response status:", error.response?.status);
      console.error("Response headers:", error.response?.headers);
      setErrorMessage(
        error.response?.data?.message || 
        error.message || 
        "Upload failed. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-8">
            <div className="card shadow-sm">
              <div className="card-header bg-primary text-white">
                <h4 className="mb-0">Upload Document</h4>
              </div>
              <div className="card-body">
                <div className="text-center mb-4">
                  <p className="lead">Upload a file to check for AI-generated content</p>
                  <small className="text-muted">Supported formats: .txt, .doc, .docx, .pdf</small>
                </div>

                {errorMessage && (
                  <div className="alert alert-danger" role="alert">
                    {errorMessage}
                  </div>
                )}

                <div className="mb-4">
                  <div className="custom-file">
                    <input 
                      type="file" 
                      className="form-control" 
                      accept=".txt,.docx,.doc,.pdf" 
                      onChange={handleFileChange}
                      disabled={loading}
                    />
                  </div>
                </div>

                {file && (
                  <div className="mb-3">
                    <p className="mb-1">Selected file: <strong>{file.name}</strong></p>
                    <p className="text-muted mb-0">Size: {(file.size / 1024 / 1024).toFixed(2)} MB</p>
                  </div>
                )}

                {loading && uploadProgress > 0 && (
                  <div className="mb-3">
                    <label className="form-label">Upload Progress</label>
                    <div className="progress">
                      <div 
                        className="progress-bar progress-bar-striped progress-bar-animated" 
                        role="progressbar" 
                        style={{ width: `${uploadProgress}%` }}
                        aria-valuenow={uploadProgress} 
                        aria-valuemin="0" 
                        aria-valuemax="100"
                      >
                        {uploadProgress}%
                      </div>
                    </div>
                  </div>
                )}

                <div className="d-grid gap-2">
                  <button 
                    className="btn btn-primary btn-lg"
                    onClick={handleUpload}
                    disabled={loading || !file}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Analyzing...
                      </>
                    ) : (
                      'Upload & Analyze'
                    )}
                  </button>
                </div>
              </div>
            </div>

            <div className="card mt-4 shadow-sm">
              <div className="card-body">
                <h5 className="card-title">Recent Activity</h5>
                <p className="card-text">
                  View your recent document analysis history and statistics in the{' '}
                  <a href="/history">History</a> section.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
