import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';

function History() {
  const navigate = useNavigate();
  const [history, setHistory] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        setLoading(true);
        const userStr = localStorage.getItem('user');
        if (!userStr) {
          setError('User not logged in');
          setLoading(false);
          return;
        }

        const user = JSON.parse(userStr);
        const response = await api.get(`/files/history/${user.id}`);
        
        if (response.data && response.data.uploads) {
          setHistory(response.data.uploads);
        } else {
          setError('No history data found');
        }
      } catch (error) {
        console.error('Error fetching history:', error);
        setError(error.response?.data?.error || 'Failed to load history');
      } finally {
        setLoading(false);
      }
    };

    fetchHistory();
  }, []);

  if (!localStorage.getItem('user')) {
    navigate('/');
    return null;
  }

  return (
    <div className="container mt-5">
      <h3>Your Upload History</h3>
      {error && <div className="alert alert-danger">{error}</div>}
      {loading ? (
        <div className="text-center mt-4">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <>
          {history.length === 0 ? (
            <div className="alert alert-info mt-3">No upload history found.</div>
          ) : (
            <div className="table-responsive">
              <table className="table mt-3">
                <thead>
                  <tr>
                    <th>Filename</th>
                    <th>AI Content %</th>
                    <th>Upload Date</th>
                  </tr>
                </thead>
                <tbody>
                  {history.map((item, index) => (
                    <tr key={index}>
                      <td>{item.filename}</td>
                      <td>{item.percentAI}%</td>
                      <td>{new Date(item.timestamp).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </>
      )}
      <button className="btn btn-secondary mt-3" onClick={() => navigate('/dashboard')}>
        Back to Dashboard
      </button>
    </div>
  );
}

export default History;
