import React from 'react';
import { useLocation, Link } from 'react-router-dom';

const Result = () => {
  const { state } = useLocation();
  console.log('Result state:', state); // Add logging to debug

  if (!state) {
    return (
      <div className="container mt-5">
        <h4>No result data found.</h4>
        <Link to="/dashboard" className="btn btn-secondary mt-3">Back to Dashboard</Link>
      </div>
    );
  }

  const highlightContent = () => {
    if (!state.content || !state.aiParts) return 'No content to analyze';
    let output = '';
    state.content.split(' ').forEach((word, idx) => {
      if (state.aiParts.includes(idx)) {
        output += `<span style="background-color: #ffcccc">${word}</span> `;
      } else {
        output += `${word} `;
      }
    });
    return output;
  };

  return (
    <div className="container mt-5">
      <div className="card p-4">
        <h3>Analysis Result</h3>
        <div className="mb-3">
          <p><strong>File:</strong> {state.fileName}</p>
          <p><strong>AI-generated Content:</strong> {Math.round(state.similarityScore * 100)}%</p>
        </div>
        
        <div className="mt-4">
          <h4>Content Analysis</h4>
          <div className="border p-3 mt-3" style={{ whiteSpace: 'pre-wrap', backgroundColor: '#fff' }}
            dangerouslySetInnerHTML={{ __html: highlightContent() }}>
          </div>
        </div>
        
        <div className="mt-4">
          <Link to="/dashboard" className="btn btn-success">Upload Another File</Link>
          {' '}
          <Link to="/history" className="btn btn-secondary">View History</Link>
        </div>
      </div>
    </div>
  );
};

export default Result;
