import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import AdminNavbar from './AdminNavbar';
import api from '../../api/api';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
} from 'chart.js';

// Register ChartJS components
ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

function AdminDashboard() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [stats, setStats] = useState({
        totalUsers: 0,
        totalDocuments: 0,
        totalScans: 0,
        averageAiScore: 0
    });
    const [recentActivity, setRecentActivity] = useState([]);
    const [activityTrend, setActivityTrend] = useState({
        labels: [],
        datasets: []
    });
    const admin = JSON.parse(localStorage.getItem('admin') || '{}');

    useEffect(() => {
        if (!admin.id) {
            navigate('/admin/login');
            return;
        }
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async () => {
        try {
            const [statsRes, activityRes, trendRes] = await Promise.all([
                api.get('/admin/dashboard/stats'),
                api.get('/admin/recent-activity'),
                api.get('/admin/activity-trend')
            ]);

            setStats(statsRes.data);
            setRecentActivity(activityRes.data);
            setActivityTrend({
                labels: trendRes.data.labels,
                datasets: [{
                    label: 'Document Scans',
                    data: trendRes.data.scans,
                    borderColor: 'rgb(53, 162, 235)',
                    backgroundColor: 'rgba(53, 162, 235, 0.5)',
                }]
            });
        } catch (err) {
            setError('Failed to load dashboard data');
            console.error('Dashboard error:', err);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <>
                <AdminNavbar />
                <div className="container mt-4">
                    <div className="text-center">
                        <div className="spinner-border text-primary" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                </div>
            </>
        );
    }

    return (
        <>
            <AdminNavbar />
            <div className="container mt-4">
                {error && (
                    <div className="alert alert-danger" role="alert">
                        {error}
                    </div>
                )}

                <div className="row mb-4">
                    <div className="col-12">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h2 className="card-title">Welcome, {admin.fullName}</h2>
                                <p className="text-muted">Role: {admin.role}</p>
                                <p className="text-muted">Email: {admin.email}</p>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Quick Stats */}
                <div className="row mb-4">
                    <div className="col-md-3">
                        <div className="card bg-primary text-white shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Total Users</h5>
                                <h2 className="mb-0">{stats.totalUsers}</h2>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card bg-success text-white shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Documents Scanned</h5>
                                <h2 className="mb-0">{stats.totalDocuments}</h2>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card bg-info text-white shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Total Scans</h5>
                                <h2 className="mb-0">{stats.totalScans}</h2>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card bg-warning text-white shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Avg. AI Score</h5>
                                <h2 className="mb-0">{stats.averageAiScore}%</h2>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Activity Chart */}
                <div className="row mb-4">
                    <div className="col-12">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Activity Trend</h5>
                                <Line data={activityTrend} options={{
                                    responsive: true,
                                    plugins: {
                                        legend: {
                                            position: 'top',
                                        },
                                        title: {
                                            display: true,
                                            text: 'Document Scans Over Time'
                                        }
                                    }
                                }} />
                            </div>
                        </div>
                    </div>
                </div>

                {/* Quick Actions & Recent Activity */}
                <div className="row">
                    <div className="col-md-4 mb-4">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Quick Actions</h5>
                                <div className="list-group">
                                    <Link to="/admin/users" className="list-group-item list-group-item-action">
                                        <i className="bi bi-people me-2"></i>
                                        Manage Users
                                    </Link>
                                    <Link to="/admin/documents" className="list-group-item list-group-item-action">
                                        <i className="bi bi-file-text me-2"></i>
                                        View Documents
                                    </Link>
                                    <Link to="/admin/analytics" className="list-group-item list-group-item-action">
                                        <i className="bi bi-graph-up me-2"></i>
                                        View Analytics
                                    </Link>
                                    <Link to="/admin/settings" className="list-group-item list-group-item-action">
                                        <i className="bi bi-gear me-2"></i>
                                        System Settings
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-8 mb-4">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Recent Activity</h5>
                                <div className="table-responsive">
                                    <table className="table table-hover">
                                        <thead>
                                            <tr>
                                                <th>User</th>
                                                <th>Action</th>
                                                <th>Document</th>
                                                <th>Time</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {recentActivity.map((activity, index) => (
                                                <tr key={index}>
                                                    <td>{activity.userEmail}</td>
                                                    <td>{activity.action}</td>
                                                    <td>{activity.documentName}</td>
                                                    <td>{new Date(activity.timestamp).toLocaleString()}</td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AdminDashboard;
