import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend);

const Result = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  if (!state) {
    return (
      <>
        <Navbar />
        <div className="container mt-5">
          <div className="alert alert-warning">
            No result data found. Please upload a document first.
          </div>
          <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
            Back to Dashboard
          </button>
        </div>
      </>
    );
  }

  const aiPercentage = Math.round(state.similarityScore * 100);
  const humanPercentage = 100 - aiPercentage;

  const chartData = {
    labels: ['AI-Generated', 'Human-Written'],
    datasets: [
      {
        data: [aiPercentage, humanPercentage],
        backgroundColor: [
          'rgba(255, 99, 132, 0.8)',
          'rgba(54, 162, 235, 0.8)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const highlightContent = () => {
    if (!state.content || !state.aiParts) return 'No content to analyze';
    let output = '';
    state.content.split(' ').forEach((word, idx) => {
      if (state.aiParts.includes(idx)) {
        output += `<span class="ai-text">${word}</span> `;
      } else {
        output += `<span class="human-text">${word}</span> `;
      }
    });
    return output;
  };
  const exportAsPDF = async () => {    // Initialize PDF with optimal quality settings
    const pdf = new jsPDF({
      unit: 'pt',
      format: 'a4',
      putOnlyUsedFonts: true,
      compress: false  // Disable compression for better text quality
    });
    
    // PDF layout configuration
    const pageWidth = pdf.internal.pageSize.getWidth();
    const pageHeight = pdf.internal.pageSize.getHeight();
    const margin = 72;  // Standard 1-inch margin
    const contentWidth = pageWidth - (2 * margin);
    let currentY = margin;
    const baseLineHeight = 24;  // 2x font size for line height
    const wordSpacing = 6;      // Increased word spacing for better readability
    
    // Enhanced text rendering settings
    pdf.setLineWidth(0.2);
    pdf.setDrawColor(33, 37, 41);
    pdf.setTextColor(33, 37, 41);
    pdf.setFont('helvetica', 'normal');
    pdf.setFontSize(12);
      // Helper function for optimal text rendering
    const renderText = (text, x, y, isAI = false) => {
      // Ensure integer coordinates
      const xPos = Math.floor(x);
      const yPos = Math.floor(y);
      
      if (isAI) {
        // Calculate exact text dimensions
        const wordWidth = Math.ceil(pdf.getStringUnitWidth(text) * pdf.getFontSize());
        const textHeight = pdf.getFontSize();
        
        // Draw highlight with more padding
        pdf.setFillColor(252, 232, 232);
        pdf.rect(
          xPos - 3,
          yPos - textHeight,
          wordWidth + 6,  // More horizontal padding
          textHeight + 6,  // More vertical padding
          'F'
        );
        pdf.setTextColor(180, 32, 32);
        pdf.setDrawColor(180, 32, 32);
      } else {
        pdf.setTextColor(33, 37, 41);
        pdf.setDrawColor(33, 37, 41);
      }
      
      // Enhanced text rendering for better clarity
      pdf.setLineWidth(0.2);
      pdf.internal.write('2 Tr');
      
      // Add slight offset for better text positioning
      pdf.text(text, xPos, Math.floor(yPos) + 0.5);
      
      pdf.internal.write('0 Tr');
    };

    // Helper function to add new page with consistent styling
    const addNewPage = () => {
      pdf.addPage();
      pdf.setFillColor(13, 110, 253);
      pdf.rect(0, 0, pageWidth, 60, 'F');
      for(let i = 0; i < 5; i++) {
        pdf.setFillColor(13, 110, 253, (5-i)/10);
        pdf.rect(0, 60 + i*3, pageWidth, 3, 'F');
      }
      pdf.setTextColor(255, 255, 255);
      pdf.setFontSize(12);
      pdf.text('PlagiGuard Report - Continued', margin, 35);
      currentY = 100;
    };

    // Create header with gradient
    pdf.setFillColor(13, 110, 253);
    pdf.rect(0, 0, pageWidth, 120, 'F');
    for(let i = 0; i < 5; i++) {
      pdf.setFillColor(13, 110, 253, (5-i)/10);
      pdf.rect(0, 120 + i*3, pageWidth, 3, 'F');
    }

    // Title
    pdf.setTextColor(255, 255, 255);
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(28);
    pdf.text('PlagiGuard Report', margin, 70);
    pdf.setFontSize(14);
    pdf.text('AI Content Detection Analysis', margin, 95);
    currentY = 160;

    // Document Info Section
    pdf.setFillColor(248, 249, 250);
    pdf.setDrawColor(222, 226, 230);
    pdf.roundedRect(margin - 10, currentY - 10, contentWidth + 20, 100, 3, 3, 'FD');
    
    pdf.setTextColor(33, 37, 41);
    pdf.setFontSize(18);
    pdf.setFont('helvetica', 'bold');
    pdf.text('Document Information', margin, currentY + 20);
    
    pdf.setFontSize(12);
    pdf.setFont('helvetica', 'normal');
    pdf.text(`File Name: ${state.fileName}`, margin + 10, currentY + 45);
    pdf.text(`Analysis Date: ${new Date().toLocaleString()}`, margin + 10, currentY + 65);
    currentY += 120;

    // Content Analysis Section
    pdf.setFillColor(255, 255, 255);
    pdf.roundedRect(margin - 10, currentY - 10, contentWidth + 20, 180, 3, 3, 'FD');
    
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(18);
    pdf.text('Content Analysis', margin, currentY + 20);

    pdf.setFontSize(14);
    pdf.setTextColor(220, 53, 69);
    pdf.text(`AI Generated Content: ${aiPercentage}%`, margin + 10, currentY + 50);
    pdf.setTextColor(13, 110, 253);
    pdf.text(`Human Written Content: ${humanPercentage}%`, margin + 10, currentY + 75);

    // Add pie chart
    const canvas = document.querySelector('canvas');
    if (canvas) {
      const chartImg = canvas.toDataURL('image/png', 1.0);
      pdf.addImage(chartImg, 'PNG', pageWidth - 220, currentY + 20, 140, 140);
    }
    currentY += 200;

    // Content Review Section
    pdf.setTextColor(33, 37, 41);
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(18);
    pdf.text('Content Review', margin, currentY);
    currentY += 30;

    // Info box
    pdf.setFillColor(232, 244, 253);
    pdf.setTextColor(13, 110, 253);
    pdf.roundedRect(margin - 5, currentY - 5, contentWidth + 10, 40, 3, 3, 'F');
    pdf.setFontSize(11);
    pdf.text('Note: Text highlighted in red indicates potential AI-generated content.', margin + 10, currentY + 20);
    currentY += 60;
    
    // Process content with enhanced text rendering
    pdf.setTextColor(33, 37, 41);
    pdf.setFont('helvetica', 'normal');
    pdf.setFontSize(12);
    
    // Process content paragraph by paragraph for better structure
    const paragraphs = state.content.split('\n\n');
    let wordOffset = 0;
    
    paragraphs.forEach((paragraph, paragraphIndex) => {
      if (paragraph.trim() === '') return;
      
      const words = paragraph.split(' ').filter(w => w.trim());
      let line = '';
      let lineWords = [];
      let xPos = margin + 10;
      
      words.forEach((word, idx) => {
        const testLine = line + (line ? ' ' : '') + word;
        const testWidth = Math.ceil(pdf.getStringUnitWidth(testLine) * pdf.getFontSize());
        
        if (testWidth > contentWidth - 50) {  // Increased margin for safety
          // Improved spacing calculation
          const totalSpaces = lineWords.length - 1;
          const totalWidth = Math.ceil(pdf.getStringUnitWidth(line) * pdf.getFontSize());
          const availableSpace = contentWidth - 50 - totalWidth;
          const extraSpacing = totalSpaces > 0 ? 
            Math.min(availableSpace / totalSpaces, 10) : 0;  // Limited max spacing
          
          // Render current line with improved positioning
          xPos = margin + 10;
          lineWords.forEach((w, i) => {
            const isWordAI = state.aiParts.includes(wordOffset + idx - lineWords.length + i);
            renderText(w, xPos, currentY, isWordAI);
            
            // Calculate next word position with consistent spacing
            const wordWidth = Math.ceil(pdf.getStringUnitWidth(w) * pdf.getFontSize());
            xPos += wordWidth + wordSpacing + (i < lineWords.length - 1 ? extraSpacing : 0);
          });
          
          currentY += baseLineHeight;
          if (currentY > pageHeight - margin) {
            addNewPage();
          }
          
          line = word;
          lineWords = [word];
        } else {
          line = testLine;
          lineWords.push(word);
        }
      });
      
      // Handle last line with improved spacing
      if (lineWords.length > 0) {
        xPos = margin + 10;
        lineWords.forEach((word, i) => {
          const isWordAI = state.aiParts.includes(
            wordOffset + words.length - lineWords.length + i
          );
          renderText(word, xPos, currentY, isWordAI);
          const wordWidth = Math.ceil(pdf.getStringUnitWidth(word) * pdf.getFontSize());
          xPos += wordWidth + wordSpacing;
        });
      }
      
      wordOffset += words.length;
      currentY += baseLineHeight * 1.5;  // Extra spacing between paragraphs
      if (currentY > pageHeight - margin) {
        addNewPage();
      }
    });

    pdf.save(`PlagiGuard_Report_${state.fileName}.pdf`);
  };

  return (
    <>
      <Navbar />
      <div className="container mt-5" id="report-content">
        <div className="card">
          <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
            <h3 className="mb-0">Analysis Result</h3>
            <button className="btn btn-outline-light" onClick={exportAsPDF}>
              Export as PDF
            </button>
          </div>
          
          <div className="card-body">
            <div className="row">
              <div className="col-md-6">
                <div className="mb-4">
                  <h4>Document Information</h4>
                  <p><strong>File Name:</strong> {state.fileName}</p>
                  <p><strong>Analysis Date:</strong> {new Date().toLocaleString()}</p>
                </div>
                
                <div className="mb-4">
                  <h4>Content Analysis</h4>
                  <p><strong>AI Content Detection:</strong> {aiPercentage}%</p>
                  <p><strong>Human Content Detection:</strong> {humanPercentage}%</p>
                </div>
              </div>
              
              <div className="col-md-6">
                <div style={{ maxWidth: '400px', margin: '0 auto' }}>
                  <Pie data={chartData} />
                </div>
              </div>
            </div>

            <div className="mt-4">
              <h4>Content Review</h4>
              <div className="alert alert-info">
                <small>
                  Highlighted text indicates potential AI-generated content.
                  Please note that this is an automated analysis and may not be 100% accurate.
                </small>
              </div>
              <div 
                className="border p-3 mt-3 content-area"
                style={{ 
                  whiteSpace: 'pre-wrap',
                  backgroundColor: '#fff',
                  maxHeight: '400px',
                  overflowY: 'auto'
                }}
                dangerouslySetInnerHTML={{ __html: highlightContent() }}
              />
            </div>
          </div>
        </div>

        <div className="mt-4 d-flex gap-2">
          <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
            Upload Another File
          </button>
          <button className="btn btn-secondary" onClick={() => navigate('/history')}>
            View History
          </button>
        </div>
      </div>

      <style>{`
        .ai-text {
          color: #dc3545;  /* Bootstrap red */
          background-color: rgba(220, 53, 69, 0.1);
          padding: 2px 4px;
          border-radius: 3px;
          transition: background-color 0.2s;
        }
        .ai-text:hover {
          background-color: rgba(220, 53, 69, 0.2);
        }
        .human-text {
          color: #0d6efd;  /* Bootstrap blue */
          background-color: rgba(13, 110, 253, 0.1);
          padding: 2px 4px;
          border-radius: 3px;
          transition: background-color 0.2s;
        }
        .human-text:hover {
          background-color: rgba(13, 110, 253, 0.2);
        }
        .content-area {
          line-height: 2;
          font-size: 1.1rem;
          border-radius: 8px;
          box-shadow: inset 0 0 10px rgba(0,0,0,0.1);
        }
      `}</style>
    </>
  );
};

export default Result;
