import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend);

const Result = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  if (!state) {
    return (
      <>
        <Navbar />
        <div className="container mt-5">
          <div className="alert alert-warning">
            No result data found. Please upload a document first.
          </div>
          <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
            Back to Dashboard
          </button>
        </div>
      </>
    );
  }

  const aiPercentage = Math.round(state.similarityScore * 100);
  const humanPercentage = 100 - aiPercentage;

  const chartData = {
    labels: ['AI-Generated', 'Human-Written'],
    datasets: [
      {
        data: [aiPercentage, humanPercentage],
        backgroundColor: [
          'rgba(255, 99, 132, 0.8)',
          'rgba(54, 162, 235, 0.8)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const highlightContent = () => {
    if (!state.content || !state.aiParts) return 'No content to analyze';
    let output = '';
    state.content.split(' ').forEach((word, idx) => {
      if (state.aiParts.includes(idx)) {
        output += `<span class="ai-text">${word}</span> `;
      } else {
        output += `<span class="human-text">${word}</span> `;
      }
    });
    return output;
  };  const exportAsPDF = async () => {
    // Create PDF with higher quality settings
    const pdf = new jsPDF({
      unit: 'pt',
      format: 'a4',
      compress: false
    });
    
    const pageWidth = pdf.internal.pageSize.getWidth();
    const pageHeight = pdf.internal.pageSize.getHeight();
    const margin = 40;
    const contentWidth = pageWidth - 2 * margin;
    let currentY = margin;
    const lineHeight = 16;
    
    // Add fancy header with design
    pdf.setFillColor(13, 110, 253); // Bootstrap primary blue
    pdf.rect(0, 0, pageWidth, 80, 'F');
    pdf.setTextColor(255, 255, 255);
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(32);
    pdf.text('PlagiGuard Report', margin, 50);
    currentY = 100;

    // Add decorative line
    pdf.setDrawColor(220, 53, 69); // Bootstrap red
    pdf.setLineWidth(2);
    pdf.line(margin, currentY - 10, pageWidth - margin, currentY - 10);
    
    // Document Information section
    pdf.setTextColor(33, 37, 41);
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(20);
    pdf.text('Document Information', margin, currentY);
    currentY += lineHeight * 2;

    // Info box with light background
    pdf.setFillColor(248, 249, 250); // Bootstrap light gray
    pdf.rect(margin, currentY, contentWidth, lineHeight * 4, 'F');
    pdf.setFontSize(12);
    pdf.text(`File Name: ${state.fileName}`, margin + 10, currentY + lineHeight);
    pdf.text(`Analysis Date: ${new Date().toLocaleString()}`, margin + 10, currentY + lineHeight * 2);
    currentY += lineHeight * 5;

    // Content Analysis with chart on right
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(20);
    pdf.text('Content Analysis', margin, currentY);
    currentY += lineHeight * 2;    // Calculate chart dimensions first
    const chartWidth = 120; // Made smaller
    const chartHeight = 120;
    
    // Analysis text on left
    pdf.setFontSize(14);
    pdf.setTextColor(220, 53, 69); // Red for AI
    pdf.text(`AI Content: ${aiPercentage}%`, margin, currentY);
    pdf.setTextColor(13, 110, 253); // Blue for Human
    pdf.text(`Human Content: ${humanPercentage}%`, margin, currentY + lineHeight);
    
    // Add pie chart on right side with drop shadow
    const canvas = document.querySelector('canvas');
    if (canvas) {
      // Add drop shadow
      pdf.setDrawColor(0);
      pdf.setFillColor(248, 249, 250);
      pdf.roundedRect(
        pageWidth - margin - chartWidth - 10,
        currentY - lineHeight * 2 - 5,
        chartWidth + 20,
        chartHeight + 10,
        3,
        3,
        'FD'
      );
      
      const chartImg = canvas.toDataURL('image/png', 1.0);
      pdf.addImage(
        chartImg,
        'PNG',
        pageWidth - margin - chartWidth - 5,
        currentY - lineHeight * 2,
        chartWidth,
        chartHeight
      );
    }
