import axios from 'axios';

// Define the base URL for the API
const BASE_URL = 'http://localhost:8085';

// Create axios instance with custom config
const api = axios.create({
  baseURL: BASE_URL,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  },
  timeout: 5000, // 5 second timeout
  validateStatus: function (status) {
    return status >= 200 && status < 500; // Handle all responses
  }
});

// Add request interceptor to handle authentication
api.interceptors.request.use(
  (config) => {
    // Get the auth tokens from localStorage
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const admin = JSON.parse(localStorage.getItem('admin') || '{}');
    
    // Add token based on the request path
    if (config.url?.startsWith('/api/admin/') && admin?.token) {
      config.headers.Authorization = `Bearer ${admin.token}`;
    } else if (user?.token) {
      config.headers.Authorization = `Bearer ${user.token}`;
    }

    // For debugging
    console.log('Making request to:', config.baseURL + config.url);
    console.log('Request headers:', config.headers);
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    // Check if error is due to authentication
    if (error.response?.status === 401) {
      // Clear tokens and redirect based on path
      if (error.config.url?.startsWith('/api/admin/')) {
        localStorage.removeItem('admin');
        window.location.href = '/admin/login';
      } else {
        localStorage.removeItem('user');
        window.location.href = '/login';
      }
    }
    console.error('Response error:', error?.response?.data || error.message);
    return Promise.reject(error);
  }
);

export default api;
