import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const navigate = useNavigate();
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');

  const user = JSON.parse(localStorage.getItem('user'));

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);
    formData.append('userId', user.id);

    try {
      const res = await axios.post('http://localhost:8080/api/upload', formData);
      if (res.data.success) {
        localStorage.setItem('result', JSON.stringify(res.data));
        navigate('/result');
      } else {
        setMessage('Failed to process the file.');
      }
    } catch (err) {
      setMessage('Server error during upload.');
    }
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3>Welcome, {user.name}</h3>
        <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
      </div>
      {message && <div className="alert alert-warning">{message}</div>}
      <form onSubmit={handleUpload}>
        <div className="mb-3">
          <label>Upload File for AI Check</label>
          <input type="file" className="form-control" required onChange={handleFileChange} />
        </div>
        <button type="submit" className="btn btn-primary">Check Plagiarism</button>
      </form>
      <hr />
      <button className="btn btn-secondary mt-3" onClick={() => navigate('/history')}>View History</button>
    </div>
  );
}

export default Dashboard;
