import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import Navbar from './Navbar';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { Line } from 'react-chartjs-2';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function History() {
  const navigate = useNavigate();
  const [history, setHistory] = useState([]);
  const [filteredHistory, setFilteredHistory] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    aiScoreMin: '',
    aiScoreMax: '',
    dateFrom: '',
    dateTo: ''
  });
  const [sortConfig, setSortConfig] = useState({ key: 'timestamp', direction: 'desc' });

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        setLoading(true);
        const user = JSON.parse(localStorage.getItem('user'));
        if (!user) {
          navigate('/');
          return;
        }        const response = await api.get(`/api/files/history/${user.id}`, {
          headers: {
            "Authorization": `Bearer ${user.token}`
          }
        });
        if (response.data && response.data.uploads) {
          const uploads = response.data.uploads.map(item => ({
            ...item,
            percentAI: item.percentAI || 0,
            timestamp: new Date(item.timestamp).toISOString()
          }));
          setHistory(uploads);
          setFilteredHistory(uploads);
          } else {
            setHistory([]);
            setFilteredHistory([]);
          }
      } catch (error) {
        console.error('Error fetching history:', error);
        setError(error.response?.data?.error || 'Failed to load history');
      } finally {
        setLoading(false);
      }
    };

    fetchHistory();
  }, [navigate]);

  useEffect(() => {
    applyFilters();
  }, [filters, history]);

  const applyFilters = () => {
    let filtered = [...history];

    // Apply search filter
    if (filters.search) {
      filtered = filtered.filter(item =>
        item.filename.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Apply AI score range filter
    if (filters.aiScoreMin !== '') {
      filtered = filtered.filter(item => item.percentAI >= parseInt(filters.aiScoreMin));
    }
    if (filters.aiScoreMax !== '') {
      filtered = filtered.filter(item => item.percentAI <= parseInt(filters.aiScoreMax));
    }

    // Apply date range filter
    if (filters.dateFrom) {
      filtered = filtered.filter(item => 
        new Date(item.timestamp) >= new Date(filters.dateFrom)
      );
    }
    if (filters.dateTo) {
      filtered = filtered.filter(item => 
        new Date(item.timestamp) <= new Date(filters.dateTo)
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      if (sortConfig.key === 'timestamp') {
        return sortConfig.direction === 'asc' 
          ? new Date(a.timestamp) - new Date(b.timestamp)
          : new Date(b.timestamp) - new Date(a.timestamp);
      }
      if (sortConfig.key === 'percentAI') {
        return sortConfig.direction === 'asc'
          ? a.percentAI - b.percentAI
          : b.percentAI - a.percentAI;
      }
      return 0;
    });

    setFilteredHistory(filtered);
  };

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const handleSort = (key) => {
    setSortConfig({
      key,
      direction: sortConfig.key === key && sortConfig.direction === 'asc' ? 'desc' : 'asc'
    });
  };

  const getChartData = () => {
    const data = {
      labels: filteredHistory.map(item => new Date(item.timestamp).toLocaleDateString()),
      datasets: [{
        label: 'AI Content Percentage',
        data: filteredHistory.map(item => item.percentAI),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }]
    };
    return data;
  };

  if (!localStorage.getItem('user')) {
    navigate('/');
    return null;
  }

  return (
    <>
      <Navbar />
      <div className="container mt-5">
        <div className="card">
          <div className="card-header bg-primary text-white">
            <h3 className="mb-0">Upload History</h3>
          </div>
          <div className="card-body">
            {error && <div className="alert alert-danger">{error}</div>}
            
            {/* Filters */}
            <div className="row mb-4">
              <div className="col-md-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search by filename"
                  name="search"
                  value={filters.search}
                  onChange={handleFilterChange}
                />
              </div>
              <div className="col-md-2">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Min AI %"
                  name="aiScoreMin"
                  value={filters.aiScoreMin}
                  onChange={handleFilterChange}
                />
              </div>
              <div className="col-md-2">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Max AI %"
                  name="aiScoreMax"
                  value={filters.aiScoreMax}
                  onChange={handleFilterChange}
                />
              </div>
              <div className="col-md-2">
                <input
                  type="date"
                  className="form-control"
                  name="dateFrom"
                  value={filters.dateFrom}
                  onChange={handleFilterChange}
                />
              </div>
              <div className="col-md-2">
                <input
                  type="date"
                  className="form-control"
                  name="dateTo"
                  value={filters.dateTo}
                  onChange={handleFilterChange}
                />
              </div>
            </div>

            {/* Trend Chart */}
            {filteredHistory.length > 0 && (
              <div className="mb-4">
                <h4>AI Content Trend</h4>
                <div style={{ height: '300px' }}>
                  <Line data={getChartData()} options={{ maintainAspectRatio: false }} />
                </div>
              </div>
            )}

            {/* Results Table */}
            {loading ? (
              <div className="text-center mt-4">
                <div className="spinner-border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
              </div>
            ) : (
              <>
                {filteredHistory.length === 0 ? (
                  <div className="alert alert-info mt-3">No upload history found.</div>
                ) : (
                  <div className="table-responsive">
                    <table className="table table-hover">
                      <thead>
                        <tr>
                          <th>Filename</th>
                          <th onClick={() => handleSort('percentAI')} style={{ cursor: 'pointer' }}>
                            AI Content %
                            {sortConfig.key === 'percentAI' && (
                              <i className={`ms-1 bi bi-arrow-${sortConfig.direction === 'asc' ? 'up' : 'down'}`}></i>
                            )}
                          </th>
                          <th onClick={() => handleSort('timestamp')} style={{ cursor: 'pointer' }}>
                            Upload Date
                            {sortConfig.key === 'timestamp' && (
                              <i className={`ms-1 bi bi-arrow-${sortConfig.direction === 'asc' ? 'up' : 'down'}`}></i>
                            )}
                          </th>
                        </tr>
                      </thead>
                      <tbody>
                        {filteredHistory.map((item, index) => (
                          <tr key={index}>
                            <td>{item.filename}</td>
                            <td>
                              <div className="progress" style={{ height: '20px' }}>
                                <div
                                  className={`progress-bar ${item.percentAI > 70 ? 'bg-danger' : 'bg-success'}`}
                                  role="progressbar"
                                  style={{ width: `${item.percentAI}%` }}
                                >
                                  {item.percentAI}%
                                </div>
                              </div>
                            </td>
                            <td>{new Date(item.timestamp).toLocaleString()}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default History;
