import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';

function History() {
  const navigate = useNavigate();
  const [history, setHistory] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        setLoading(true);
        const user = JSON.parse(localStorage.getItem('user'));
        const response = await api.get(`/api/files/history/${user.id}`);
        setHistory(response.data.uploads || []);
      } catch (error) {
        console.error('Error fetching history:', error);
        setError(error.response?.data?.error || 'Failed to load history');
      } finally {
        setLoading(false);
      }
    };

    fetchHistory();
  }, []);

  return (
    <div className="container mt-5">
      <h3>Your Upload History</h3>
      {error && <div className="alert alert-danger">{error}</div>}
      {loading ? (
        <div className="text-center mt-4">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <>
          {history.length === 0 ? (
            <div className="alert alert-info mt-3">No upload history found.</div>
          ) : (
            <table className="table mt-3">
              <thead>
                <tr>
                  <th>Filename</th>
                  <th>AI %</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {history.map((item, index) => (
                  <tr key={index}>
                    <td>{item.filename}</td>
                    <td>{item.percentAI}%</td>
                    <td>{new Date(item.timestamp).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </>
      )}
      <button className="btn btn-secondary mt-3" onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
    </div>
  );
}

export default History;
