import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend);

const Result = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  if (!state) {
    return (
      <>
        <Navbar />
        <div className="container mt-5">
          <div className="alert alert-warning">
            No result data found. Please upload a document first.
          </div>
          <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
            Back to Dashboard
          </button>
        </div>
      </>
    );
  }

  const aiPercentage = Math.round(state.similarityScore * 100);
  const humanPercentage = 100 - aiPercentage;

  const chartData = {
    labels: ['AI-Generated', 'Human-Written'],
    datasets: [
      {
        data: [aiPercentage, humanPercentage],
        backgroundColor: [
          'rgba(255, 99, 132, 0.8)',
          'rgba(54, 162, 235, 0.8)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const highlightContent = () => {
    if (!state.content || !state.aiParts) return 'No content to analyze';
    let output = '';
    state.content.split(' ').forEach((word, idx) => {
      if (state.aiParts.includes(idx)) {
        output += `<span class="ai-text">${word}</span> `;
      } else {
        output += `<span class="human-text">${word}</span> `;
      }
    });
    return output;
  };
  const exportAsPDF = async () => {
    // Create PDF with better quality settings
    const pdf = new jsPDF({
      unit: 'pt',
      format: 'a4',
      compress: false
    });
    
    const pageWidth = pdf.internal.pageSize.getWidth();
    const pageHeight = pdf.internal.pageSize.getHeight();
    const margin = 50;
    const contentWidth = pageWidth - 2 * margin;
    let currentY = margin;
    const lineHeight = 16;

    // Add fancy header with gradient-like effect
    pdf.setFillColor(13, 110, 253); // Bootstrap primary blue
    pdf.rect(0, 0, pageWidth, 100, 'F');
    for(let i = 0; i < 5; i++) {
      pdf.setFillColor(13, 110, 253, 0.1);
      pdf.rect(0, 100 + i*2, pageWidth, 2, 'F');
    }
    
    // Add logo/title
    pdf.setTextColor(255, 255, 255);
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(32);
    pdf.text('PlagiGuard Report', margin, 60);
    pdf.setFontSize(14);
    pdf.text('AI Content Detection Analysis', margin, 85);
    currentY = 140;

    // Document Information section with styled box
    pdf.setDrawColor(220, 53, 69); // Bootstrap red
    pdf.setFillColor(248, 249, 250); // Light gray background
    pdf.roundedRect(margin - 10, currentY - 20, contentWidth + 20, 100, 5, 5, 'FD');
    
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(20);
    pdf.setTextColor(33, 37, 41);
    pdf.text('Document Information', margin, currentY);
    currentY += lineHeight * 1.5;

    pdf.setFontSize(12);
    pdf.setFont('helvetica', 'bold');
    pdf.text('File Name:', margin + 10, currentY + 5);
    pdf.setFont('helvetica', 'normal');
    pdf.text(state.fileName, margin + 100, currentY + 5);
    currentY += lineHeight * 1.5;

    pdf.setFont('helvetica', 'bold');
    pdf.text('Analysis Date:', margin + 10, currentY + 5);
    pdf.setFont('helvetica', 'normal');
    pdf.text(new Date().toLocaleString(), margin + 100, currentY + 5);
    currentY += lineHeight * 4;

    // Content Analysis section with pie chart
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(20);
    pdf.text('Content Analysis', margin, currentY);
    currentY += lineHeight * 2;

    // Get pie chart
    const canvas = document.querySelector('canvas');
    if (canvas) {
      // Add stats on the left
      pdf.setFontSize(14);
      pdf.setTextColor(220, 53, 69); // Red for AI
      pdf.text(`AI Generated Content: ${aiPercentage}%`, margin, currentY + 30);
      pdf.setTextColor(13, 110, 253); // Blue for Human
      pdf.text(`Human Written Content: ${humanPercentage}%`, margin, currentY + 50);

      // Add chart on the right with shadow effect
      const chartWidth = 150;
      const chartHeight = 150;
      
      // Draw shadow
      pdf.setFillColor(0, 0, 0, 0.1);
      pdf.roundedRect(pageWidth - margin - chartWidth - 15, currentY - 5, chartWidth + 30, chartHeight + 10, 5, 5, 'F');
      
      // Draw chart background
      pdf.setFillColor(255, 255, 255);
      pdf.roundedRect(pageWidth - margin - chartWidth - 20, currentY - 10, chartWidth + 30, chartHeight + 10, 5, 5, 'F');
      
      const chartImg = canvas.toDataURL('image/png', 1.0);
      pdf.addImage(chartImg, 'PNG', pageWidth - margin - chartWidth - 5, currentY, chartWidth, chartHeight);
      currentY += chartHeight + lineHeight * 2;
    }

    // Content Review section
    currentY += lineHeight * 2;
    pdf.setDrawColor(220, 53, 69);
    pdf.setLineWidth(2);
    pdf.line(margin, currentY, pageWidth - margin, currentY);
    currentY += lineHeight * 2;

    pdf.setTextColor(33, 37, 41);
    pdf.setFont('helvetica', 'bold');
    pdf.setFontSize(20);
    pdf.text('Content Review', margin, currentY);
    currentY += lineHeight * 2;

    // Add info box
    pdf.setFillColor(232, 244, 253); // Light blue background
    pdf.setTextColor(13, 110, 253);
    pdf.roundedRect(margin, currentY - 5, contentWidth, lineHeight * 3, 3, 3, 'F');
    pdf.setFontSize(11);
    pdf.text('Note: Highlighted text indicates potential AI-generated content.', margin + 10, currentY + lineHeight);
    currentY += lineHeight * 4;

    // Content container
    pdf.setDrawColor(220, 53, 69);
    pdf.setFillColor(252, 252, 252);
    const contentBoxY = currentY;
    let contentHeight = pageHeight - currentY - margin;
    pdf.roundedRect(margin - 10, contentBoxY, contentWidth + 20, contentHeight, 5, 5, 'FD');
      // Process content with justified text
    pdf.setTextColor(33, 37, 41);
    pdf.setFont('helvetica', 'normal');
    pdf.setFontSize(12); // Increased font size for better readability
    
    const content = state.content;
    const paragraphs = content.split('\n');
    const textMargin = margin + 15;
    const effectiveWidth = contentWidth - 30;
    
    // Function to add a new page with consistent styling
    const addNewPage = () => {
      pdf.addPage();
      
      // Add header
      pdf.setFillColor(13, 110, 253);
      pdf.rect(0, 0, pageWidth, 40, 'F');
      pdf.setTextColor(255, 255, 255);
      pdf.setFontSize(14);
      pdf.text('PlagiGuard Report - Continued', margin, 25);
      
      // Add content container
      pdf.setDrawColor(220, 53, 69);
      pdf.setFillColor(252, 252, 252);
      pdf.roundedRect(margin - 10, margin + 20, contentWidth + 20, pageHeight - (margin * 2) - 20, 5, 5, 'FD');
      
      currentY = margin + 50; // Start below header
      
      // Reset text settings
      pdf.setTextColor(33, 37, 41);
      pdf.setFontSize(12);
    };
    
    paragraphs.forEach((paragraph, pIndex) => {
      // Add new page if we're close to the bottom
      if (currentY > pageHeight - margin * 2) {
        addNewPage();
      }
      
      if (paragraph.trim() === '') {
        currentY += lineHeight;
        return;
      }

      currentY += lineHeight * 0.5;
      const words = paragraph.split(' ');
      
      words.forEach((word, idx) => {
        const isAI = state.aiParts.includes(idx);
        const testLine = line + (line ? ' ' : '') + word;
        const testWidth = pdf.getStringUnitWidth(testLine) * pdf.getFontSize();
        
        if (testWidth > effectiveWidth) {
          if (lineWords.length > 1) {
            const spaceWidth = (effectiveWidth - pdf.getStringUnitWidth(line) * pdf.getFontSize()) / (lineWords.length - 1);
            let xPos = textMargin;
            
            lineWords.forEach((w, i) => {
              const isWordAI = state.aiParts.includes(idx - lineWords.length + i);
              if (isWordAI) {
                const wordWidth = pdf.getStringUnitWidth(w) * pdf.getFontSize();
                pdf.setFillColor(255, 235, 235);
                pdf.rect(xPos - 1, currentY - pdf.getFontSize() + 2, wordWidth + 2, pdf.getFontSize() + 2, 'F');
                pdf.setTextColor(220, 53, 69);
                pdf.setFontSize(12);
              } else {
                pdf.setTextColor(33, 37, 41);
                pdf.setFontSize(11);
              }
              
              pdf.text(w, xPos, currentY);
              xPos += pdf.getStringUnitWidth(w) * pdf.getFontSize() + spaceWidth;
            });
          }
          
          currentY += lineHeight;
          if (currentY > pageHeight - margin) {
            pdf.addPage();
            currentY = margin + 40;
            
            // Add header to new page
            pdf.setFillColor(13, 110, 253);
            pdf.rect(0, 0, pageWidth, 40, 'F');
            pdf.setTextColor(255, 255, 255);
            pdf.setFontSize(14);
            pdf.text('PlagiGuard Report - Continued', margin, 25);
            
            // Reset text settings
            pdf.setTextColor(33, 37, 41);
            pdf.setFontSize(11);
          }
          
          line = word;
          lineWords = [word];
        } else {
          line = testLine;
          lineWords.push(word);
        }
      });
      
      // Handle last line of paragraph
      if (lineWords.length > 0) {
        let xPos = textMargin;
        lineWords.forEach((word) => {
          const isWordAI = state.aiParts.includes(state.content.split(' ').indexOf(word));
          if (isWordAI) {
            const wordWidth = pdf.getStringUnitWidth(word) * pdf.getFontSize();
            pdf.setFillColor(255, 235, 235);
            pdf.rect(xPos - 1, currentY - pdf.getFontSize() + 2, wordWidth + 2, pdf.getFontSize() + 2, 'F');
            pdf.setTextColor(220, 53, 69);
            pdf.setFontSize(12);
          } else {
            pdf.setTextColor(33, 37, 41);
            pdf.setFontSize(11);
          }
          pdf.text(word, xPos, currentY);
          xPos += pdf.getStringUnitWidth(word + ' ') * pdf.getFontSize();
        });
      }
      
      currentY += lineHeight * 1.2;
    });
    
    pdf.save(`PlagiGuard_Report_${state.fileName}.pdf`);
  };

  return (
    <>
      <Navbar />
      <div className="container mt-5" id="report-content">
        <div className="card">
          <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
            <h3 className="mb-0">Analysis Result</h3>
            <button className="btn btn-outline-light" onClick={exportAsPDF}>
              Export as PDF
            </button>
          </div>
          
          <div className="card-body">
            <div className="row">
              <div className="col-md-6">
                <div className="mb-4">
                  <h4>Document Information</h4>
                  <p><strong>File Name:</strong> {state.fileName}</p>
                  <p><strong>Analysis Date:</strong> {new Date().toLocaleString()}</p>
                </div>
                
                <div className="mb-4">
                  <h4>Content Analysis</h4>
                  <p><strong>AI Content Detection:</strong> {aiPercentage}%</p>
                  <p><strong>Human Content Detection:</strong> {humanPercentage}%</p>
                </div>
              </div>
              
              <div className="col-md-6">
                <div style={{ maxWidth: '400px', margin: '0 auto' }}>
                  <Pie data={chartData} />
                </div>
              </div>
            </div>

            <div className="mt-4">
              <h4>Content Review</h4>
              <div className="alert alert-info">
                <small>
                  Highlighted text indicates potential AI-generated content.
                  Please note that this is an automated analysis and may not be 100% accurate.
                </small>
              </div>
              <div 
                className="border p-3 mt-3 content-area"
                style={{ 
                  whiteSpace: 'pre-wrap',
                  backgroundColor: '#fff',
                  maxHeight: '400px',
                  overflowY: 'auto'
                }}
                dangerouslySetInnerHTML={{ __html: highlightContent() }}
              />
            </div>
          </div>
        </div>

        <div className="mt-4 d-flex gap-2">
          <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
            Upload Another File
          </button>
          <button className="btn btn-secondary" onClick={() => navigate('/history')}>
            View History
          </button>
        </div>
      </div>

      <style>{`
        .ai-text {
          color: #dc3545;  /* Bootstrap red */
          background-color: rgba(220, 53, 69, 0.1);
          padding: 2px 4px;
          border-radius: 3px;
          transition: background-color 0.2s;
        }
        .ai-text:hover {
          background-color: rgba(220, 53, 69, 0.2);
        }
        .human-text {
          color: #0d6efd;  /* Bootstrap blue */
          background-color: rgba(13, 110, 253, 0.1);
          padding: 2px 4px;
          border-radius: 3px;
          transition: background-color 0.2s;
        }
        .human-text:hover {
          background-color: rgba(13, 110, 253, 0.2);
        }
        .content-area {
          line-height: 2;
          font-size: 1.1rem;
          border-radius: 8px;
          box-shadow: inset 0 0 10px rgba(0,0,0,0.1);
        }
      `}</style>
    </>
  );
};

export default Result;
